const BlogsMenu = () => {
  return (
      <div className='blogs-menu-container'>
        {blogs.map(blog => (
          <Card 
            key={blog.id}
            title={blog.title} 
            date={blog.date} 
            image='http://unvab.com/snow-free-html/assets/images/post-1-mid.jpg' 
            description={blog.description} 
          />
        ))}
      </div>
  )
}

export default BlogsMenu
