import React, { useRef, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";

import "./Slider.scss";
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"

import SliderContent from './SliderContent'

import SwiperCore, {
  Pagination
} from 'swiper/core';

SwiperCore.use([Pagination]);

const Slider = () => {
  return (
    <Swiper pagination={true} className="swiper">
      <SwiperSlide>
        <SliderContent />
      </SwiperSlide>
      <SwiperSlide>
        <quote className='swiper__quote'>
          “Outstanding job and exceeded all expectations. 
          It was a pleasure to work with them on a sizable first project
          and am looking forward to start the next one asap.“
        </quote>
        <p className='swiper__author'>Michael Hopkins</p>
      </SwiperSlide>
      <SwiperSlide>
        <quote className='swiper__quote'>
          “Outstanding job and exceeded all expectations. 
          It was a pleasure to work with them on a sizable first project
          and am looking forward to start the next one asap.“
        </quote>
        <p className='swiper__author'>Michael Hopkins</p>
      </SwiperSlide>
      <SwiperSlide>Slide 4</SwiperSlide>
      <SwiperSlide>Slide 5</SwiperSlide>
    </Swiper>
  )
}

export default Slider;
