import Card from '../../UI/Card/Card'

import './BlogsMenu.scss'

const blogs = [
  {title: 'Why you should always first', date: 'August 18, 2019', description: 'Quisque cursus risus id ante fermentum, in auctor quam consectetur. Vestibulum est nisi, tempus ac vehicula sit amet, blandit et sapien. Ut congue dui enim, at viverra nisl tempor a.'},
  {title: 'Something I need to tell you', date: 'June 18, 2012', description: 'Pellentesque viverra nisl a erat volutpat commodo. Etiam sem justo, pellentesque egestas finibus at, rutrum efficitur ex. Donec facilisis magna in mauris fermentum, sit amet porttitor lorem dictum. '},
  {title: 'History of nature', date: 'April 21, 2017', description: 'Cras varius odio a arcu rutrum, nec posuere lacus imperdiet. Proin iaculis, nibh eleifend elementum pulvinar, erat nisl consequat quam, ac ornare est sem nec libero. Fusce ac sagittis quam. '},
]

const BlogsMenu = () => {
  return (
      <div>
        {blogs.map(blog => (
          <Card 
            title={blog.title} 
            date={blog.date} 
            image='http://unvab.com/snow-free-html/assets/images/post-1-mid.jpg' 
            description={blog.description} 
          />
        ))}
      </div>
  )
}

export default BlogsMenu
